import { Component, OnDestroy } from '@angular/core';
import { map, Observable, Subscription } from 'rxjs';
import { RickAndMortyService } from './rick-and-morty.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnDestroy {
  title = 'ProgramacionReactiva-Caldaroni';

  character$: Subscription;
  character: any;
  characterO$: Observable<any>;

  constructor(private rickAndMortyService: RickAndMortyService) {
    this.characterO$ = this.rickAndMortyService.getCharacter(2)
      .pipe(
        map((x: any) => {
          return {name: x.name, image: x.image}
        })
      );
    this.character$ = this.rickAndMortyService.getCharacter(1)
      .pipe(
        map((x: any) => {
          return {name: x.name, image:x.image}
        })
      ).subscribe(
      (character: any) => {
        this.character = character;
      }
    );
  }
  ngOnDestroy(): void {
    this.character$.unsubscribe();
  }

}
<p>
  <mat-toolbar>
    <span>Programacion Reactiva-Caldaroni</span>
  </mat-toolbar>
</p>

<div layout="row" layout-align="space-around center">
  <div>
    <mat-card class="example-card " *ngIf="character" >
      <mat-card-header>
        <mat-card-title>{{character.name}}</mat-card-title>
      </mat-card-header>
      <img mat-card-image [src]="character.image" alt="Photo of a Shiba Inu">
      <mat-card-content>

      </mat-card-content>
    </mat-card>
  </div>
  <div>
    <mat-card class="example-card" *ngIf="characterO$ | async as ch">
      <mat-card-header>
        <mat-card-title>{{ch.name}}</mat-card-title>
      </mat-card-header>
      <img mat-card-image [src]="ch.image" alt="Photo of a Shiba Inu">
      <mat-card-content>

      </mat-card-content>
    </mat-card>
  </div>
</div>
